import React from "react";

class StorePicker extends React.Component {
  render() {
    return (
      <form>
        {/* Hi */}
        <h2> Please Enter A Store</h2>
        <input
          type="text"
          name="name"
          id="name"
          className="form-control"
          placeholder="Name"
          value={this.props.name}
          onChange={this.props.onChange}
        />
      </form>
    );
  }
}

export default StorePicker;
